SELECT * FROM ( ( SELECT f.order_no, f.weight, a.created_time, b.handle_no, b.driver, e.id next_station, b.third_express_code, b.handle_name, f.order_type, f.reference_no, f. STATUS, d.contact_number AS phone, d.address, d. NAME AS receive_name, f.parent_no, e.name next_station_name FROM t_handle_third_express_details a INNER JOIN t_handle_third_express b ON a.third_handle_no = b.handle_no INNER JOIN t_waybill c ON a.order_no = c.order_no INNER JOIN t_waybill_receiver d ON a.order_no = d.order_no LEFT JOIN t_bas_network e ON e.id = c.next_network_id LEFT JOIN t_waybill f ON a.order_no = f.parent_no and f.is_package != "1" WHERE a.merchant_id = #{merchantId} AND b. STATUS = 1 AND b.type = 'package'
ORDER BY a.created_time DESC ) UNION ALL ( SELECT c.order_no, a.weight, a.created_time, b.handle_no, b.driver, e.id next_station, b.third_express_code, b.handle_name, c.order_type, c.reference_no, c. STATUS, d.contact_number AS phone, d.address, d. NAME AS receive_name, c.parent_no, e.name next_station_name FROM t_handle_third_express_details a INNER JOIN t_handle_third_express b ON a.third_handle_no = b.handle_no INNER JOIN t_waybill c ON a.order_no = c.order_no INNER JOIN t_waybill_receiver d ON a.order_no = d.order_no LEFT JOIN t_bas_network e ON e.id = c.next_network_id WHERE a.merchant_id = #{merchantId} AND b. STATUS = 1 AND c.is_package = "1" AND b.type = 'package' ORDER BY a.created_time DESC ) ) AS d
WHERE 1
and d.order_no LIKE CONCAT( CONCAT('%',#{orderNo}),'%')
and d.driver = #{driver}
and d.next_station = #{nextStationCode}
and d.handle_no = #{handleNo}
and d.order_type = #{orderType}
and d.created_time >= #{fromCreatedTime}
and d.created_time <= #{toCreatedTime}
and d.status = #{status}
order by d.created_time desc
limit
#{offset} ,
#{limit}

