SELECT * FROM ( SELECT a.handle_no, a.order_no, b.occur_num, b.created_time, 'riderDelivery' AS 'type', e.`name` handle_name, '' AS 'third_express_code', '' AS 'network_code', d.reference_no, d.order_type, d.weight, d.`status`, '' AS 'network', g.contact_number AS phone, g.address, g. NAME AS receive_name, f.`name` rider FROM t_handle_rider_details AS a INNER JOIN ( SELECT a.order_no, count(a.id) AS occur_num, max(a.created_time) AS created_time FROM t_handle_rider_details AS a INNER JOIN t_handle_rider AS c ON a.handle_no = c.handle_no AND c.`status` = 1 GROUP BY a.order_no HAVING COUNT(a.order_no) > 1 ) AS b ON a.order_no = b.order_no AND a.created_time = b.created_time INNER JOIN t_handle_rider AS c ON a.handle_no = c.handle_no AND c.`status` = 1 AND c.merchant_id = #{merchantId} INNER JOIN t_waybill AS d ON a.order_no = d.order_no INNER JOIN t_sys_user_info AS e ON c.handle_by = e.id INNER JOIN t_sys_user_info AS f ON c.rider = f.id INNER JOIN t_waybill_receiver AS g ON a.order_no = g.order_no UNION SELECT a.third_handle_no AS handle_no, a.order_no, b.occur_num, b.created_time, c.type, c.handle_name, c.third_express_code, c.network_code, d.reference_no, d.order_type, d.weight, d.`status`, e.`name` network, f.contact_number AS phone, f.address, f. NAME AS receive_name, '' AS 'rider' FROM t_handle_third_express_details AS a INNER JOIN ( SELECT a.order_no, count(a.id) AS occur_num, max(a.created_time) AS created_time FROM t_handle_third_express_details as a INNER JOIN t_handle_third_express AS c ON a.third_handle_no = c.handle_no and c.`status`=1 GROUP BY a.order_no HAVING COUNT(a.order_no) > 1 ) AS b ON a.order_no = b.order_no AND a.created_time = b.created_time INNER JOIN t_handle_third_express AS c ON a.third_handle_no = c.handle_no AND c.`status` = 1 AND c.merchant_id = #{merchantId} INNER JOIN t_waybill AS d ON a.order_no = d.order_no LEFT JOIN t_bas_network AS e ON e.id = c.network_code INNER JOIN t_waybill_receiver AS f ON a.order_no = f.order_no ) AS a
WHERE 1
and a.order_no LIKE CONCAT( CONCAT('%',#{orderNo}),'%')
and a.network = #{nextStation}
AND a.network_code = #{nextStationCode}
and a.handle_no = #{handleNo}
and a.order_type = #{orderType}
and a.type = #{dispatchType}
and a.created_time >= #{fromCreatedTime}
and a.created_time <= #{toCreatedTime}
and a.status = #{status}
order by a.created_time desc
limit
#{offset} ,
#{limit}

SELECT count(1) FROM ( SELECT a.handle_no, a.order_no, b.occur_num, b.created_time, 'riderDelivery' AS 'type', e.`name` handle_name, '' AS 'third_express_code', d.reference_no, d.order_type, d.weight, d.`status`, '' AS 'network', g.contact_number AS phone, g.address, g. NAME AS receive_name, f.`name` rider FROM t_handle_rider_details AS a INNER JOIN ( SELECT order_no, count(1) AS occur_num, max(created_time) AS created_time FROM t_handle_rider_details GROUP BY order_no HAVING COUNT(order_no) > 2 ) AS b ON a.order_no = b.order_no AND a.created_time = b.created_time INNER JOIN t_handle_rider AS c ON a.handle_no = c.handle_no AND c.`status` = 1 AND c.merchant_id = 1 INNER JOIN t_waybill AS d ON a.order_no = d.order_no INNER JOIN t_sys_user_info AS e ON c.handle_by = e.id INNER JOIN t_sys_user_info AS f ON c.rider = f.id INNER JOIN t_waybill_receiver AS g ON a.order_no = g.order_no UNION SELECT a.third_handle_no AS handle_no, a.order_no, b.occur_num, b.created_time, c.type, c.handle_name, c.third_express_code, d.reference_no, d.order_type, d.weight, d.`status`, e.`name` network, f.contact_number AS phone, f.address, f. NAME AS receive_name, '' AS 'rider' FROM t_handle_third_express_details AS a INNER JOIN ( SELECT order_no, count(1) AS occur_num, max(created_time) AS created_time FROM t_handle_third_express_details GROUP BY order_no HAVING COUNT(order_no) > 2 ) AS b ON a.order_no = b.order_no AND a.created_time = b.created_time INNER JOIN t_handle_third_express AS c ON a.third_handle_no = c.handle_no AND c.`status` = 1 AND c.merchant_id = 1 INNER JOIN t_waybill AS d ON a.order_no = d.order_no LEFT JOIN t_bas_network AS e ON e.id = c.network_code INNER JOIN t_waybill_receiver AS f ON a.order_no = f.order_no ) AS a
WHERE 1
and a.order_no LIKE CONCAT( CONCAT('%',#{orderNo}),'%')
and a.network = #{nextStation}
and a.handle_no = #{handleNo}
and a.order_type = #{orderType}
and a.created_time >= #{fromCreatedTime}
and a.created_time <= #{toCreatedTime}
and a.status = #{status}

SELECT a.order_no,a.merchant_id,a.order_platform,a.order_platform,a.created_time, a.updated_time, a.weight, d.name
FROM t_waybill a
INNER JOIN
t_waybill_receiver b
on a.order_no = b.order_no
INNER JOIN t_waybill_task c on b.order_no = c.order_no
INNER JOIN t_sys_user_info d
on c.handled_by=d.id
WHERE a.merchant_id=#{merchantId}
AND a.order_no = #{orderNo}
AND (a.created_time<#{eTime} AND a.created_time>#{sTime})
AND DATE_FORMAT(FROM_UNIXTIME(a.created_time),"%Y%m") = #{mother}
AND a.order_platform = #{clientName}
order by a.created_time desc limit #{offset} , #{limit}
