SELECT b.STATUS, a.order_no, b.reference_no, b.weight, d.name AS sign_name, a.sign_time as handle_time, a.sign_handle_by_name as handle_by_name, a.sign_handle_by_id, a.sign_time, a.sign_network_id as network_code, a.sign_network_name as network_name, a.last_deliver_express_code, a.sign_outsource_name as outsource, a.last_deliver_network_name as rider, a.last_deliver_express_code as express_code, c.`name` sname, d.`name` rname, d.contact_number, d.address FROM t_report_waybill a INNER JOIN t_waybill b ON a.order_no = b.order_no and a.sign_time is not null INNER JOIN t_waybill_sender c ON a.order_no = c.order_no INNER JOIN t_waybill_receiver d ON a.order_no = d.order_no
WHERE b.merchant_id=#{merchantId}
and a.order_no LIKE CONCAT( CONCAT('%',#{orderNo}),'%')
and a.sign_time >= #{fromHandledTime}
and a.sign_time <= #{toHandledTime}
and a.last_deliver_rider_id = #{riderId}
and a.sign_outsource_code = #{outsource}
and a.last_deliver_express_code = #{networkCode}
and a.last_deliver_express_code = #{expressName}
and ( a.network_code in
#{item}
)
order by a.sign_time desc limit #{offset} , #{limit}

