SELECT a. STATUS, a.order_no, a.reference_no, a.weight, a.need_pay_amount, a.already_paid, b.signer AS sign_name, b.handle_by, b.handle_time, b.remark, b.network_code, b.express_code, c.`name` sname, d.`name` rname, d.contact_number, d.address,
CONCAT_WS('-',e.staff_id, e.real_name) AS rider, e.outsource,
f.name network_name FROM t_waybill a INNER JOIN t_handle_sign b ON a.order_no = b.order_no INNER JOIN t_waybill_sender c ON b.order_no = c.order_no INNER JOIN t_waybill_receiver d ON c.order_no = d.order_no LEFT JOIN t_bas_staff e ON b.handle_by = e.user_id INNER JOIN t_bas_network f ON f.id = b.network_code WHERE a.merchant_id=#{merchantId}
and a.order_no LIKE CONCAT( CONCAT('%',#{orderNo}),'%')
and b.handle_time >= #{fromHandledTime}
and b.handle_time <= #{toHandledTime}
and b.handle_by = #{riderId}
and e.outsource = #{outsource}
and b.network_code = #{networkCode}
and b.express_code = #{expressName}
and ( b.network_code in
#{item}
)
order by b.handle_time desc limit #{offset} , #{limit}

        select
        COUNT(1)
        from
        t_waybill a
        INNER JOIN t_handle_sign b ON a.order_no = b.order_no
        INNER JOIN t_waybill_sender c ON b.order_no = c.order_no
        INNER JOIN t_waybill_receiver d ON c.order_no = d.order_no
        LEFT JOIN t_bas_staff e ON b.handle_by = e.user_id
        INNER JOIN t_bas_network f ON f.id = b.network_code
        WHERE a.merchant_id=#{merchantId}
        
            and a.order_no LIKE CONCAT( CONCAT('%',#{orderNo}),'%')
        
        
            and b.handle_time >= #{fromHandledTime}
        
        
            and   b.handle_time <= #{toHandledTime} 
        
        
            and  b.handle_by = #{riderId}
        
        
            and e.outsource = #{outsource}
        
        
            and  b.network_code = #{networkCode}
        
        
            and  b.express_code = #{expressName}
        
        
            and (
            b.network_code in
            
                #{item}
            
            )
